    private void autonumber(){
         String ID = "USER000";
         int i = 0;
         try{
             
             Statement st = conn.createStatement();
             String sql = "select id_user from t_user";
             ResultSet rs = st.executeQuery(sql);
             while(rs.next()){
             ID = rs.getString("id_user");
             
         }
             ID = ID.substring(4);
             i = Integer.parseInt(ID)+1;
             ID = "00" + i;
             ID = "USER" + ID.substring(ID.length()-3);
             tid1.setText(ID);
         }catch (SQLException e){
             System.out.println(e.getMessage());
         }
     }
       public void datatable () {
        
        Object[] Baris = {"ID USER"," NAMA LENGKAP","USERNAME","PASSWORD","E-MAIL","USER TYPE"};
        tabmode = new DefaultTableModel(null, Baris);
        tadmin1.setModel(tabmode);
        String sql = "select * from t_user";
        
        try {
                java.sql.Statement stat = conn.createStatement();
                ResultSet hasil = stat.executeQuery(sql);
                while (hasil.next()){
                    String a = hasil.getString("id_user");
                    String b = hasil.getString("nama_lengkap");
                    String c = hasil.getString("username");
                    String d = hasil.getString("password");
                    String e = hasil.getString("email");
                    String f = hasil.getString("usertype");
                    
                  
                    
                    String[] data={a,b,c,d,e,f};
                    tabmode.addRow(data);
                    
   
                }
            } catch (Exception e) {
        }
    }
