    private void tadmin1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tadmin1MouseClicked
        // TODO add your handling code here:
        int bar = tadmin1.getSelectedRow();
        String a = tabmode.getValueAt(bar,0).toString();
        String b = tabmode.getValueAt(bar,1).toString();
        String c = tabmode.getValueAt(bar,2).toString();
        String d = tabmode.getValueAt(bar,3).toString();
        String e = tabmode.getValueAt(bar,4).toString();
        String f = tabmode.getValueAt(bar,5).toString();

        tid1.setText(a);
        tnama1.setText(b);
        tUser1.setText(c);
        tPass1.setText(d);
        tmail1.setText(e);
        tuser1.setText(f);
    }//GEN-LAST:event_tadmin1MouseClicked

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        try {

            String sql = "update t_user set id_user=?, nama_lengkap =?,username=?, password=?, email=?, usertype=? where id_user='"+tid1.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);

            stat.setString(1, tid1.getText());
            stat.setString(2, tnama1.getText());
            stat.setString(3, tUser1.getText());
            stat.setString(4, tPass1.getText());
            stat.setString(5, tmail1.getText());
            stat.setString(6, tuser1.getText());;

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosong();
            tid1.requestFocus();
            datatable();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
       int ok = JOptionPane.showConfirmDialog(null, "Hapus","Konfirmasi Dialog",JOptionPane.YES_NO_CANCEL_OPTION);
        if (ok==0) {
            String sql = "delete from t_user where id_user = '"+tid1.getText()+"'";
            try {
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null,"Data Berhasil Di Hapus");
                kosong();
                tid1.requestFocus();
                datatable();
            } catch (SQLException e) {
                JOptionPane.showMessageDialog(null,"Data Gagal Di Hapus"+e);
            }
        }
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String sql = "INSERT INTO t_user VALUES (?,?,?,?,?,?)";
        try{
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tid1.getText());
            stat.setString(2, tnama1.getText());
            stat.setString(3, tUser1.getText());
            stat.setString(4, tPass1.getText());
            stat.setString(5, tmail1.getText());
            stat.setString(6, tuser1.getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "DATA Berhasil Disimpan");
            kosong();
            datatable();
            autonumber();
            tid1.requestFocus();

        }catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan "+e);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

