         tabmode = new DefaultTableModel(null, Baris);
            tadmin1.setModel(tabmode);
         String sql = "select * from t_user where username like '%"+txcari.getText()+"%'";
         try {
                java.sql.Statement stat = conn.createStatement();
                ResultSet hasil = stat.executeQuery(sql);
                while (hasil.next()){
                    String a = hasil.getString("id_user");
                    String b = hasil.getString("nama_lengkap");
                    String c = hasil.getString("username");
                    String d = hasil.getString("password");
                    String e = hasil.getString("email");
                    String f = hasil.getString("usertype");

                    String[] data={a,b,c,d,e,f};
                    tabmode.addRow(data);
                }
            } catch (Exception e) {
        }
