    protected void aktif() {
        tid.setEnabled(true);
        tnama.setEnabled(true);
  
        twali.getSelectedItem();
        tid.requestFocus();
    }

    protected void kosong() {
        tid.setText("");
        tnama.setText("");
       
        twali.setSelectedItem("Cari Wali Kelas");
    }
    
     protected void datatable () {
        
        Object[] Baris = {"ID KELAS","NAMA KELAS","NAMA WALI KELAS"};
        tabmode = new DefaultTableModel(null, Baris);
        tkelas.setModel(tabmode);
        String sql = "select * from t_kelas";
        
        try {
                java.sql.Statement stat = conn.createStatement();
                ResultSet hasil = stat.executeQuery(sql);
                while (hasil.next()){
                    String a = hasil.getString("id");
                    String b = hasil.getString("kelas");
                    
                    String c = hasil.getString("wali_kelas");
                   
                    String[] data={a,b,c};
                    tabmode.addRow(data);
                }
            } catch (Exception e) {
        }
    }

     
     public void tampil_combo(){
    
   
   
    try{
        
       String sql= "select * From t_guru ";
        java.sql.Statement stat = conn.createStatement();
        java.sql.PreparedStatement pst = conn.prepareStatement(sql);
        ResultSet rs = pst.executeQuery();
        
        while(rs.next()){
            twali.addItem(rs.getString("nama_guru"));
            
        }
        rs.last();
        int jumlahdata = rs.getRow();
        rs.first();
        
    }catch(Exception e){
        
    }
}
