    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
         try {
             
            String sql = "update t_kelas set id=?, kelas =?, wali_kelas=? where id='"+tid.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);

            stat.setString(1,tid.getText());
            stat.setString(2,tnama.getText());
            
            stat.setString(3,twali.getSelectedItem().toString());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosong();
            tid.requestFocus();
            datatable();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String sql = "insert into t_kelas values (?,?,?)";
        try {
            PreparedStatement stat = conn.prepareStatement(sql);

            stat.setString(1,tid.getText());
            stat.setString(2,tnama.getText());
         
            stat.setString(3,twali.getSelectedItem().toString());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosong();
            tid.requestFocus();
            datatable();
            tampil_combo();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void tkelasMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tkelasMouseClicked
        // TODO add your handling code here:
        int bar = tkelas.getSelectedRow();
        String a = tabmode.getValueAt(bar,0).toString();
        String b = tabmode.getValueAt(bar,1).toString();
        String c = tabmode.getValueAt(bar,2).toString();
       
       

        tid.setText(a);
        tnama.setText(b);
       
        twali.setSelectedItem(c);
        
    }//GEN-LAST:event_tkelasMouseClicked

