    private void tombolCariNilaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tombolCariNilaiActionPerformed
        // TODO add your handling code here:
        Object[] Baris = {"NIS","NAMA SISWA","KELAS","MATA PELAJARAN","GURU","SEMESTER","TUGAS","UTS","UAS","HASIL","GRADE"};
       
        tabmode = new DefaultTableModel (null, Baris);
        tabelNilaiSiswa.setModel(tabmode);

        //String sql = "SELECT data_siswa.nisn, data_siswa.nama_siswa,mapel.nama_mapel, kelas.kelas, data_guru.nama_guru,nilai_siswa.nilai_mapel from nilai_siswa INNER JOIN data_siswa on data_siswa.id_siswa = nilai_siswa.id_siswa INNER JOIN kelas ON kelas.id = data_siswa.id_kelas INNER JOIN mapel ON kelas.id = mapel.id_kelas INNER JOIN data_guru ON data_guru.id_guru = mapel.id_guru WHERE data_siswa.nisn  = "+formCariNilai.getText()+"";
        String sql = "select*from t_nilai_siswa where nis like '%"+formCariNilai.getText()+"%' and semester like'%"+csem.getSelectedItem()+"%' or nama_siswa like '%"+formCariNilai.getText()+"%' and semester like'%"+csem.getSelectedItem()+"%'";
        try {
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
             
            while(hasil.next()) {
                    
                    
                    String b = hasil.getString("nis");
                    String c = hasil.getString("nama_siswa");
                    
                    String d = hasil.getString("nama_kelas");
                    String e = hasil.getString("nama_mapel");
                    
                    String f = hasil.getString("nama_guru");
                    String g = hasil.getString("semester");
                    String h = hasil.getString("nilai_tugas");
                    String i = hasil.getString("nilai_uts");
                    String j = hasil.getString("nilai_uas");
                    String k = hasil.getString("hasil");
                    String l = hasil.getString("grade");
                
                String[] data = {b,c,d,e,f,g,h,i,j,k,l};
                
               
                { if (sql == null) {
                   JOptionPane.showMessageDialog(null,"Data Tidak Ada");
                    
                } else
                    
                    
                    tabmode.addRow(data);
                    tbl();
                }
                
            }
        } catch (Exception e) {
           
        }
        
        
        
    }//GEN-LAST:event_tombolCariNilaiActionPerformed

