    private void tombolCariabsenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tombolCariabsenActionPerformed
        // TODO add your handling code here:
        Object[] Baris = {"NIS","NAMA","KELAS","HADIR","IZIN","SAKIT","TANPA KETERANGAN","JUMLAH","TANGGAL"};
       
        tabmode = new DefaultTableModel(null, Baris);
        tabelNilaiSiswa.setModel(tabmode);
        String sql = "select k.nis_siswa,s.nama_siswa,s.nama_kelas,k.hadir,k.izin,k.sakit,k.tanpa_keterangan,k.jumlah_kehadiran, DATE_FORMAT(k.tanggal,'%y-%m-%d') AS tgl from t_siswa s INNER JOIN t_absen k on k.nis_siswa=s.NIS where s.NIS like'%"+formCariNilai1.getText()+"%' OR s.nama_siswa like'%"+formCariNilai1.getText()+"%'";
        
        try {
                java.sql.Statement stat = conn.createStatement();
                ResultSet hasil = stat.executeQuery(sql);
                while (hasil.next()){
                    String a = hasil.getString("nis_siswa");
                    String b = hasil.getString("nama_siswa");
                    String c = hasil.getString("nama_kelas");
                    String d = hasil.getString("hadir");
                    String e = hasil.getString("izin");
                    String f = hasil.getString("sakit");
                    String g = hasil.getString("tanpa_keterangan");
                    String h = hasil.getString("jumlah_kehadiran");
                    
                    String i = hasil.getString("tgl");
                    
                   
                   
                    String[] data={a,b,c,d,e,f,g,h,i};
                    tabmode.addRow(data);
                }
            } catch (Exception e) {
        }
    
        
    }//GEN-LAST:event_tombolCariabsenActionPerformed

