    private void tombolCarijadwalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tombolCarijadwalActionPerformed
        // TODO add your handling code here:
         Object[] Baris = {"NAMA MAPEL","NAMA GURU","KELAS","HARI"};
        tabmode = new DefaultTableModel(null, Baris);
        tabelNilaiSiswa.setModel(tabmode);
        String sql = "select * from t_jadwal_mapel where kelas like '%"+formCarijadwal.getText()+"%'";
        
        try {
                java.sql.Statement stat = conn.createStatement();
                ResultSet hasil = stat.executeQuery(sql);
                while (hasil.next()){
                    String a = hasil.getString("nama_mapel");
                    String b = hasil.getString("nama_guru");
                    String c = hasil.getString("kelas");
                    String d = hasil.getString("hari");
                    
                  
                    
                    String[] data={a,b,c,d};
                    tabmode.addRow(data);
                }
            } catch (Exception e) {
        }
    }//GEN-LAST:event_tombolCarijadwalActionPerformed


