     protected void kosong(){
        tid.setText("");
        tnama.setText("");
        tUser.setText("");
        tPass.setText("");
        tmail.setText("");
        tuser.setText("siswa");
       
    }

     private void autonumber(){
         String ID = "USER000";
         int i = 0;
         try{
             
             Statement st = conn.createStatement();
             String sql = "select id_user from t_user";
             ResultSet rs = st.executeQuery(sql);
             while(rs.next()){
             ID = rs.getString("id_user");
             
         }
             ID = ID.substring(4);
             i = Integer.parseInt(ID)+1;
             ID = "00" + i;
             ID = "USER" + ID.substring(ID.length()-3);
             tid.setText(ID);
         }catch (SQLException e){
             System.out.println(e.getMessage());
         }
     }
